mapper.select:
SELECT user_id , user_name,user_password,user_mail,user_state,user_reward,user_icon
from user
WHERE user_name = #{value}

SELECT user_id , user_name,user_password,user_mail,user_state
from user
WHERE user_mail = #{value}

SELECT user_id , user_name,user_password,user_mail,user_state
from user
WHERE user_id = #{value}

SELECT u.user_id,user_name,user_mail,user_state,user_reward,user_icon,l.login_ip,l.login_time
FROM user u LEFT JOIN login l on u.user_id = l.user_id
LIMIT #{start},#{showCount}
